mapper.update:
UPDATE
    TB_REVIEW_LIKE
SET REVIEW_LIKE_FLAG = #{likeFlag}
WHERE
    USER_NO = #{userNo}
AND
    REVIEW_NO = #{contentNo}

mapper.insert:
INSERT INTO TB_REVIEW_LIKE(
    USER_NO,
    REVIEW_NO,
    REVIEW_LIKE_FLAG
) VALUES (
	#{userNo},
	#{contentNo},
	#{likeFlag}
)

INSERT INTO TB_REVIEW_FILE( REVIEW_FILE_NO, REVIEW_NO, REVIEW_FILE_ORIGIN, REVIEW_FILE_UPLOAD, REVIEW_FILE_PATH, REVIEW_FILE_INDEX ) SELECT SEQ_REVIEW_FILE.NEXTVAL, SEQ_REVIEW.CURRVAL, A.REVIEW_FILE_ORIGIN, A.REVIEW_FILE_UPLOAD, A.REVIEW_FILE_PATH, A.REVIEW_FILE_INDEX FROM(
SELECT #{item.reviewFileOrigin} "REVIEW_FILE_ORIGIN",#{item.reviewFileUpload} "REVIEW_FILE_UPLOAD", #{item.reviewFilePath} "REVIEW_FILE_PATH",#{item.reviewFileIndex} "REVIEW_FILE_INDEX" FROM DUAL
) A
