mapper.delete:
DELETE
	   TB_WISHLIST
 WHERE
	   PDT_NO = #{pdtNo}
   AND
	   USER_NO = #{userNo}

mapper.update:
UPDATE
	   TB_CART
   SET
   	   CART_QUANTITY = CART_QUANTITY + #{cartAddingQuantity}
 WHERE
 	   USER_NO = #{userNo}
   AND
 	   PDT_NO = #{pdtNo}
   AND
 	   PDT_OPTION_NO = #{pdtOptionNo}
